import {
  RecoilRoot,
  atom,
  useRecoilValue, 
  useSetRecoilState,
  useResetRecoilState
}
from 'recoil'
import { useState } from 'react'

function App() {
  return (
    <RecoilRoot>
      <ItemCreator />
      <TodoList />
    </RecoilRoot>
  );
}

let idUnico = 0;

const todoListState = atom({
  key: 'todoListState',
  default: []
})

function ItemCreator(){

  const [text, setText] = useState('')
  const setNewTodo = useSetRecoilState(todoListState)

  const onChangeText= (event) => {
    setText(event.target.value);
  }

  const onClick = () => {
    setNewTodo( oldTodoList => {
      return [...oldTodoList, 
        {
          id: idUnico++, 
          text, 
          isCompleted: false
       }]

    })
    setText('')
  }

  return (
    <div>
      <input value={text} onChange={onChangeText}/>
      <button onClick={onClick}>Agregar</button>
    </div>
  )
}

/*const todos = [
  { id: 1, text: "Todo 1", isCompleted: false },
  { id: 2, text: "Todo 2", isCompleted: false },
  { id: 3, text: "Todo 3", isCompleted: true }

]*/

function TodoList(){
  const todos = useRecoilValue(todoListState)
  return ( 
  <div>
    {
      todos.map(item => <TodoItem key={item.id} {...item} />)
    }
  </div>)
}

function changeItem(id, todoList, changedItem){
  const index = todoList.findIndex(item => item.id === id)
}

function TodoItem({ id, text, isCompleted }){

  const [ todoList, setTodoList] = useResetRecoilState(todoListState)
  const onChangeTodoItem = (event) =>{

    const textValue = event.target.value

    const changedItem = {
      id: idUnico++,
      text: textValue,
      isCompleted
    }

    setTodoList(changeItem(id, todoList, changedItem))

  }

  return (
  <div>
    <input value={text} onChange={onChangeTodoItem} />
    <input type="checkbox" checked={isCompleted} />
    <button>X</button>
  </div>
  )
}

export default App;
